// src/components/Topics.jsx
import React, { useState, useEffect } from 'react';
import './Courses.css';

const Topics = () => {
  // Estado para manejar la lista de temáticas
  const [topics, setTopics] = useState([]);
  // Estado para manejar la lista de cursos
  const [courses, setCourses] = useState([]);
  // Estado para manejar el curso seleccionado
  const [selectedCourse, setSelectedCourse] = useState('');
  // Estado para manejar la temática seleccionada
  const [selectedTopic, setSelectedTopic] = useState(null);
  // Estado para manejar los cursos seleccionados en la asignación de temática
  const [selectedCourses, setSelectedCourses] = useState([]);
  // Estado para manejar la visibilidad del modal de administración
  const [showAdminModal, setShowAdminModal] = useState(false);
  // Estado para manejar la acción administrativa seleccionada
  const [adminAction, setAdminAction] = useState('');
  // Estado para manejar errores
  const [error, setError] = useState('');

  // Hook para obtener los cursos al cargar el componente
  useEffect(() => {
    fetchCourses();
  }, []);

  // Hook para obtener las temáticas cuando se selecciona un curso
  useEffect(() => {
    if (selectedCourse) {
      fetchTopics(selectedCourse);
    }
  }, [selectedCourse]);

  // Función para obtener la lista de cursos
  const fetchCourses = async () => {
    try {
      const response = await fetch('https://nodebackend-vv0e.onrender.com/api/v1/courses/');
      const data = await response.json();
      if (response.ok) {
        setCourses(data.data);
      } else {
        console.error('Error al obtener cursos:', data.message);
        setError('Error al obtener cursos.');
      }
    } catch (error) {
      console.error('Error al obtener cursos:', error);
      setError('Error al obtener cursos.');
    }
  };

  // Función para obtener las temáticas basadas en el ID del curso seleccionado
  const fetchTopics = async (courseId) => {
    try {
      const response = await fetch(`https://nodebackend-vv0e.onrender.com/api/v1/courses/find/courses_topic${courseId}`);
      const data = await response.json();
      if (response.ok) {
        setTopics(data.data.course_topic); // Ajustar según el formato de respuesta de la API
      } else {
        console.error('Error al obtener temáticas:', data.message);
        setError('Error al obtener temáticas.');
      }
    } catch (error) {
      console.error('Error al obtener temáticas:', error);
      setError('Error al obtener temáticas.');
    }
  };

  // Función para manejar la acción administrativa seleccionada
  const handleAdminAction = (topic, action) => {
    setSelectedTopic(topic);
    setAdminAction(action);
    setShowAdminModal(true);
  };

  // Función para manejar los cambios en los campos de entrada del modal de administración
  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setSelectedTopic({
      ...selectedTopic,
      [name]: value,
    });
  };

  // Función para manejar la selección de cursos en la asignación de temáticas
  const handleCourseSelection = (event) => {
    const { value, checked } = event.target;
    if (checked) {
      setSelectedCourses([...selectedCourses, value]);
    } else {
      setSelectedCourses(selectedCourses.filter(course => course !== value));
    }
  };

  // Función para enviar los datos del formulario de administración
  const handleSubmit = async (event) => {
    event.preventDefault();
    let url = '';
    let method = '';
    const payload = { ...selectedTopic };

    // Determinar la URL y el método HTTP basado en la acción administrativa seleccionada
    switch (adminAction) {
      case 'createMaterial':
        url = `https://nodebackend-vv0e.onrender.com/api/v1/courses/registerMaterialTopic`;
        payload.idTopic = selectedTopic._id;
        method = 'POST';
        break;
      case 'editTopic':
        url = `https://nodebackend-vv0e.onrender.com/api/v1/topics/${selectedTopic._id}`;
        method = 'PUT';
        break;
      case 'assignTopic':
        url = `https://nodebackend-vv0e.onrender.com/api/v1/topics/${selectedTopic._id}/assign`;
        payload.courses = selectedCourses;
        method = 'POST';
        break;
      default:
        return;
    }

    try {
      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload),
      });

      const data = await response.json();
      if (response.ok) {
        console.log('Acción realizada con éxito:', data);
        fetchTopics(selectedCourse); // Volver a obtener las temáticas basadas en el curso seleccionado
        setShowAdminModal(false);
        setSelectedTopic(null);
        setSelectedCourses([]);
      } else {
        console.error('Error al realizar la acción:', data.message);
      }
    } catch (error) {
      console.error('Error al realizar la acción:', error);
    }
  };

  return (
    <div className="topics-container">
      <h2 className="topics-heading">Lista de Temáticas AprendiX</h2>

      <div className="course-selector">
        <label htmlFor="courseSelect">Seleccionar Curso:</label>
        <select
          id="courseSelect"
          value={selectedCourse}
          onChange={(e) => setSelectedCourse(e.target.value)}
        >
          <option value="">Seleccione un curso</option>
          {courses.map((course) => (
            <option key={course._id} value={course._id}>
              {course.course_name}
            </option>
          ))}
        </select>
      </div>

      <div className="topics-grid">
        {topics.map((topic) => (
          <div key={topic._id} className="topic-card">
            <div className="topic-details">
              <h3>{topic.topic_name}</h3>
              <p><strong>Descripción:</strong> {topic.topic_description}</p>
              <div className="admin-buttons">
                <button onClick={() => handleAdminAction(topic, 'createMaterial')}>Crear Material</button>
                <button onClick={() => handleAdminAction(topic, 'editTopic')}>Editar Temática</button>
                <button onClick={() => handleAdminAction(topic, 'assignTopic')}>Asignar Temática</button>
              </div>
            </div>
          </div>
        ))}
        {topics.length === 0 && !error && (
          <p className="no-topics-message">No se encontraron temáticas.</p>
        )}
        {error && (
          <p className="error-message">{error}</p>
        )}
      </div>

      {showAdminModal && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setShowAdminModal(false)}>&times;</span>
            <h3>{adminAction === 'createMaterial' ? 'Crear Material' : adminAction === 'editTopic' ? 'Editar Temática' : 'Asignar Temática'}</h3>
            <form onSubmit={handleSubmit}>
              {adminAction === 'editTopic' && (
                <>
                  <label htmlFor="topicName">Nombre de la Temática:</label>
                  <input
                    type="text"
                    id="topicName"
                    name="topic_name"
                    value={selectedTopic.topic_name}
                    onChange={handleInputChange}
                  />
                  <label htmlFor="topicDescription">Descripción de la Temática:</label>
                  <textarea
                    id="topicDescription"
                    name="topic_description"
                    value={selectedTopic.topic_description}
                    onChange={handleInputChange}
                  />
                </>
              )}
              {adminAction === 'assignTopic' && (
                <>
                  <label>Asignar Cursos:</label>
                  {courses.map((course) => (
                    <div key={course._id}>
                      <input
                        type="checkbox"
                        id={course._id}
                        value={course._id}
                        onChange={handleCourseSelection}
                      />
                      <label htmlFor={course._id}>{course.coure_name}</label>
                    </div>
                  ))}
                </>
              )}
              <button type="submit" className="submit-button">Guardar</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Topics;
