import React, { useState } from 'react';
import { TextField, Button, Typography, Container, Box, Alert } from '@mui/material';
import { registerCourse } from '../api';

const CreateCourse = () => {
  const [courseData, setCourseData] = useState({ course_name: '', course_description: '' });
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  const handleChange = (e) => {
    setCourseData({ ...courseData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setSuccess(null);

    const correctedData = {
      coure_name: courseData.course_name,
      coure_description: courseData.course_description,
    };

    try {
      console.log('Form data before submission:', correctedData);
      const response = await registerCourse(correctedData);
      console.log('Response from API:', response);
      setSuccess('Curso creado con éxito.');
      setCourseData({ course_name: '', course_description: '' }); // Limpiar el formulario
    } catch (error) {
      console.error('Submit Error:', error);
      setError(error.response?.data?.message || 'Error al crear el curso. Por favor, inténtalo de nuevo más tarde.');
    }
  };

  return (
    <Container maxWidth="sm">
      <Box component="form" onSubmit={handleSubmit} sx={{ mt: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Crear Curso
        </Typography>
        {error && <Alert severity="error" sx={{ mt: 2 }}>{error}</Alert>}
        {success && <Alert severity="success" sx={{ mt: 2 }}>{success}</Alert>}
        <TextField
          fullWidth
          margin="normal"
          label="Nombre del curso"
          name="course_name"
          value={courseData.course_name}
          onChange={handleChange}
          required
        />
        <TextField
          fullWidth
          margin="normal"
          label="Descripción del curso"
          name="course_description"
          value={courseData.course_description}
          onChange={handleChange}
          multiline
          rows={4}
          required
        />
        <Box sx={{ mt: 2 }}>
          <Button type="submit" variant="contained" color="primary" fullWidth>
            Crear Curso
          </Button>
        </Box>
      </Box>
    </Container>
  );
};

export default CreateCourse;
