// src/components/Courses.jsx
import React, { useState, useEffect } from 'react';
import './Courses.css'; // Archivo de estilos CSS para este componente

const Courses = () => {
  const [courses, setCourses] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [newCourseData, setNewCourseData] = useState({
    coure_name: '',
    coure_description: '',
    // Otros campos del curso, si es necesario
  });

  useEffect(() => {
    // Función para cargar la lista de cursos al cargar el componente
    fetchCourses();
  }, []);

  const fetchCourses = async () => {
    try {
      const response = await fetch('https://nodebackend-vv0e.onrender.com/api/v1/courses');
      const data = await response.json();
      if (response.ok) {
        setCourses(data.data); // Suponiendo que el array de cursos está bajo la clave 'data'
      } else {
        console.error('Error al obtener cursos:', data.message);
      }
    } catch (error) {
      console.error('Error al obtener cursos:', error);
    }
  };

  const addCourse = async () => {
    try {
      const response = await fetch('https://nodebackend-vv0e.onrender.com/api/v1/courses/registerCourse', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newCourseData),
      });

      const data = await response.json();
      if (response.ok) {
        console.log('Curso agregado exitosamente:', data);
        // Volvemos a cargar la lista de cursos para mostrar el nuevo curso agregado
        fetchCourses();
        // Cerramos el modal después de agregar el curso
        setShowModal(false);
        // Reiniciamos los datos del nuevo curso
        setNewCourseData({
          coure_name: '',
          coure_description: '',
          // Otros campos del curso, si es necesario
        });
      } else {
        console.error('Error al agregar el curso:', data.message);
      }
    } catch (error) {
      console.error('Error al agregar el curso:', error);
    }
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setNewCourseData({
      ...newCourseData,
      [name]: value,
    });
  };

  return (
    <div className="users-container">
      <h2 className="users-heading">Lista de Cursos AprendiX</h2>
      <button onClick={() => setShowModal(true)}>Crear Nuevo Curso</button>
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setShowModal(false)}>&times;</span>
            <h3>Crear Nuevo Curso</h3>
            <form onSubmit={(e) => { e.preventDefault(); addCourse(); }}>
              <label htmlFor="courseName">Nombre del Curso:</label>
              <input type="text" id="courseName" name="coure_name" value={newCourseData.coure_name} onChange={handleInputChange} />
              <label htmlFor="courseDescription">Descripción del Curso:</label>
              <textarea id="courseDescription" name="coure_description" value={newCourseData.coure_description} onChange={handleInputChange} />
              {/* Agrega más campos del formulario según sea necesario */}
              <button type="submit">Agregar Curso</button>
            </form>
          </div>
        </div>
      )}
      <div className="users-grid">
        {/* Mostrar la lista de cursos */}
        {courses.map((course) => (
          <div key={course._id} className="user-card">
            <div className="user-avatar">
              {/* Puedes agregar un icono de curso aquí si lo deseas */}
            </div>
            <div className="user-details">
              <h3>{course.coure_name}</h3>
              <p><strong>Descripción:</strong> {course.coure_description}</p>
              {/* Puedes mostrar más detalles aquí si es necesario */}
            </div>
          </div>
        ))}
        {courses.length === 0 && (
          <p className="no-users-message">No se encontraron cursos.</p>
        )}
      </div>
    </div>
  );
};

export default Courses;
