// src/api.js

const API_URL = 'https://nodebackend-vv0e.onrender.com/api/v1';

const fetchApi = async (url, options) => {
  const response = await fetch(`${API_URL}${url}`, {
    ...options,
    headers: {
      'Content-Type': 'application/json',
      ...options.headers,
    },
  });

  if (!response.ok) {
    const error = await response.json();
    throw new Error(error.message);
  }

  return response.json();
};

export const login = async (email, password) => {
  return fetchApi('/auth/login', {
    method: 'POST',
    body: JSON.stringify({ email, password }),
  });
};

export const register = async (userData) => {
  return fetchApi('/auth/register', {
    method: 'POST',
    body: JSON.stringify(userData),
  });
};

export const fetchTests = async () => {
  return fetchApi('/test', { method: 'GET' });
};

export const fetchTestById = async (testId) => {
  return fetchApi(`/test/find/${testId}`, { method: 'GET' });
};

export const registerTest = async (testData) => {
  return fetchApi('/test/registerTest', {
    method: 'POST',
    body: JSON.stringify(testData),
  });
};

export const registerQuestion = async (questionData) => {
  return fetchApi('/test/registerQuestion', {
    method: 'POST',
    body: JSON.stringify(questionData),
  });
};

export const registerAnswer = async (answerData) => {
  return fetchApi('/test/registerAnswerQuestion', {
    method: 'POST',
    body: JSON.stringify(answerData),
  });
};

export const fetchCourses = async () => {
  return fetchApi('/courses', { method: 'GET' });
};

export const fetchCourseById = async (courseId) => {
  return fetchApi(`/courses/find/${courseId}`, { method: 'GET' });
};

export const registerCourse = async (courseData) => {
  return fetchApi('/courses/registerCourse', {
    method: 'POST',
    body: JSON.stringify(courseData),
  });
};

export const registerTopic = async (topicData) => {
  return fetchApi('/courses/registerTopic', {
    method: 'POST',
    body: JSON.stringify(topicData),
  });
};

export const fetchUsers = async () => {
  return fetchApi('/users', { method: 'GET' });
};

export const fetchUserById = async (userId) => {
  return fetchApi(`/users/find/${userId}`, { method: 'GET' });
};
