import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import { ThemeProvider, CssBaseline, Box } from '@mui/material';
import Encabezado from './components/Header';
import Cuerpo from './components/Body';
import Usuarios from './components/Users';
import Cursos from './components/Courses';
import Tematicas from './components/Topics';
import CrearCurso from './components/CreateCourse';
import BarraLateral from './components/BarraLateral';
import theme from './theme';

const drawerWidth = 240;

const Dashboard = () => {
  const [openCreateCourse, setOpenCreateCourse] = useState(false);

  const handleCreateCourseOpen = () => {
    setOpenCreateCourse(true);
  };

  const handleCreateCourseClose = () => {
    setOpenCreateCourse(false);
  };

  return (
    <ThemeProvider theme={theme}>
      <Router>
        <Box sx={{ display: 'flex' }}>
          <CssBaseline />
          <Encabezado />
          <BarraLateral drawerWidth={drawerWidth} />
          <Box
            component="main"
            sx={{ flexGrow: 1, p: 3, width: { sm: `calc(100% - ${drawerWidth}px)` }, marginTop: '64px' }}
          >
            <Switch>
              <Route path="/" exact component={Cuerpo} />
              <Route path="/usuarios" component={Usuarios} />
              <Route path="/cursos" component={Cursos} />
              <Route path="/tematicas" component={Tematicas} />
              <Route path="/crear-curso">
                <CrearCurso open={openCreateCourse} handleClose={handleCreateCourseClose} />
              </Route>
              {/* Agregar otras rutas aqu√≠ */}
            </Switch>
          </Box>
        </Box>
      </Router>
    </ThemeProvider>
  );
};

export default Dashboard;
