// src/components/CreateTopic.jsx
import React, { useState } from 'react';
import './CreateTopic.css';

const CreateTopic = ({ courseId, onClose }) => {
  const [topicName, setTopicName] = useState('');
  const [topicDescription, setTopicDescription] = useState('');
  const [error, setError] = useState(null);

  const token = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY2NTRiMjMyZDRmOGMxODgxMGU3OTMyOCIsImlhdCI6MTcxODE2MTQzMSwiZXhwIjoxNzE4MjQ3ODMxfQ.7--dVUCUDbRNTJ9PmQm45HEDRuXBDcBQy0mNqeSxI0c";

  const handleSubmit = async (event) => {
    event.preventDefault();
    const payload = {
      topic_name: topicName,
      topic_description: topicDescription,
      course: courseId,
    };

    try {
      const response = await fetch('https://nodebackend-vv0e.onrender.com/api/v1/courses/registerTopic', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'x-access-token': token,
        },
        body: JSON.stringify(payload),
      });

      const data = await response.json();
      if (response.ok) {
        console.log('Temática creada con éxito:', data);
        onClose();
      } else {
        console.error('Error al crear temática:', data.message);
        setError('Error al crear temática.');
      }
    } catch (error) {
      console.error('Error al crear temática:', error);
      setError('Error al crear temática.');
    }
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <span className="close" onClick={onClose}>&times;</span>
        <h3>Crear Temática</h3>
        {error && <p className="error-message">{error}</p>}
        <form onSubmit={handleSubmit}>
          <label htmlFor="topicName">Nombre de la Temática:</label>
          <input
            type="text"
            id="topicName"
            value={topicName}
            onChange={(e) => setTopicName(e.target.value)}
            className="modal-input"
          />
          <label htmlFor="topicDescription">Descripción de la Temática:</label>
          <textarea
            id="topicDescription"
            value={topicDescription}
            onChange={(e) => setTopicDescription(e.target.value)}
            className="modal-input"
          />
          <button type="submit" className="modal-submit-button">Guardar</button>
        </form>
      </div>
    </div>
  );
};

export default CreateTopic;
