// src/components/Courses.jsx
import React, { useState, useEffect } from 'react';
import { Button, Typography, Box, Alert, Modal, TextField, MenuItem, Select, InputLabel, FormControl, Checkbox, FormControlLabel } from '@mui/material';
import './Courses.css';

const Courses = () => {
  const [courses, setCourses] = useState([]);
  const [users, setUsers] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [selectedUsers, setSelectedUsers] = useState([]);
  const [showAdminModal, setShowAdminModal] = useState(false);
  const [adminAction, setAdminAction] = useState('');
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  useEffect(() => {
    fetchCourses();
    fetchUsers();
  }, []);

  const fetchCourses = async () => {
    try {
      const response = await fetch('https://nodebackend-vv0e.onrender.com/api/v1/courses');
      const data = await response.json();
      if (response.ok) {
        setCourses(data.data);
      } else {
        console.error('Error al obtener cursos:', data.message);
        setError('Error al obtener cursos.');
      }
    } catch (error) {
      console.error('Error al obtener cursos:', error);
      setError('Error al obtener cursos.');
    }
  };

  const fetchUsers = async () => {
    try {
      const response = await fetch('https://nodebackend-vv0e.onrender.com/api/v1/users'); // Asegúrate de que esta URL sea correcta
      const data = await response.json();
      if (response.ok) {
        setUsers(data.data);
      } else {
        console.error('Error al obtener usuarios:', data.message);
        setError('Error al obtener usuarios.');
      }
    } catch (error) {
      console.error('Error al obtener usuarios:', error);
      setError('Error al obtener usuarios.');
    }
  };

  const handleAdminAction = (course, action) => {
    setSelectedCourse(course);
    setAdminAction(action);
    setShowAdminModal(true);
    setSuccess(null); // Reset success message
    setError(null);   // Reset error message
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setSelectedCourse({
      ...selectedCourse,
      [name]: value,
    });
  };

  const handleUserSelection = (event) => {
    const { value, checked } = event.target;
    if (checked) {
      setSelectedUsers([...selectedUsers, value]);
    } else {
      setSelectedUsers(selectedUsers.filter(user => user !== value));
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    let url = '';
    let method = '';
    const payload = { ...selectedCourse };

    switch (adminAction) {
      case 'createTheme':
        url = `https://nodebackend-vv0e.onrender.com/api/v1/courses/registerTopic`;
        payload.course = selectedCourse._id;
        method = 'POST';
        break;
      case 'editCourse':
        url = `https://nodebackend-vv0e.onrender.com/api/v1/courses/${selectedCourse._id}`;
        method = 'PUT';
        break;
      case 'assignCourse':
        url = `https://nodebackend-vv0e.onrender.com/api/v1/courses/${selectedCourse._id}/assign`;
        payload.users = selectedUsers;
        method = 'POST';
        break;
      default:
        return;
    }

    try {
      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload),
      });

      const data = await response.json();
      if (response.ok) {
        console.log('Acción realizada con éxito:', data);
        fetchCourses();
        setShowAdminModal(false);
        setSelectedCourse(null);
        setSelectedUsers([]);
        setSuccess('Acción realizada con éxito.');
      } else {
        console.error('Error al realizar la acción:', data.message);
        setError('Error al realizar la acción.');
      }
    } catch (error) {
      console.error('Error al realizar la acción:', error);
      setError('Error al realizar la acción.');
    }
  };

  return (
    <div className="courses-container">
      <h2 className="courses-heading">Lista de Cursos AprendiX</h2>
      {error && <Alert severity="error">{error}</Alert>}
      {success && <Alert severity="success">{success}</Alert>}
      <div className="courses-grid">
        {courses.map((course) => (
          <div key={course._id} className="course-card">
            <div className="course-details">
              <h3>{course.coure_name}</h3>
              <p><strong>Descripción:</strong> {course.coure_description}</p>
              <div className="admin-buttons">
                <Button variant="contained" color="primary" onClick={() => handleAdminAction(course, 'createTheme')}>Crear Temática</Button>
                <Button variant="contained" color="secondary" onClick={() => handleAdminAction(course, 'editCourse')}>Editar Curso</Button>
                <Button variant="contained" color="default" onClick={() => handleAdminAction(course, 'assignCourse')}>Asignar Curso</Button>
              </div>
            </div>
          </div>
        ))}
        {courses.length === 0 && !error && (
          <p className="no-courses-message">No se encontraron cursos.</p>
        )}
      </div>

      <Modal open={showAdminModal} onClose={() => setShowAdminModal(false)} aria-labelledby="admin-modal-title">
        <Box
          sx={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            width: 400,
            bgcolor: 'background.paper',
            boxShadow: 24,
            p: 4,
            borderRadius: 2,
          }}
        >
          <Typography id="admin-modal-title" variant="h6" component="h2">
            {adminAction === 'createTheme' ? 'Crear Temática' : adminAction === 'editCourse' ? 'Editar Curso' : 'Asignar Curso'}
          </Typography>
          <form onSubmit={handleSubmit}>
            {adminAction === 'editCourse' && selectedCourse && (
              <>
                <TextField
                  fullWidth
                  margin="normal"
                  label="Nombre del Curso"
                  name="coure_name"
                  value={selectedCourse.coure_name}
                  onChange={handleInputChange}
                  required
                />
                <TextField
                  fullWidth
                  margin="normal"
                  label="Descripción del Curso"
                  name="coure_description"
                  value={selectedCourse.coure_description}
                  onChange={handleInputChange}
                  multiline
                  rows={4}
                  required
                />
              </>
            )}
            {adminAction === 'assignCourse' && (
              <>
                <Typography variant="subtitle1" component="h3">Asignar Usuarios:</Typography>
                {users.map((user) => (
                  <FormControlLabel
                    key={user._id}
                    control={
                      <Checkbox
                        id={user._id}
                        value={user._id}
                        onChange={handleUserSelection}
                      />
                    }
                    label={user.name}
                  />
                ))}
              </>
            )}
            <Box sx={{ mt: 2 }}>
              <Button type="submit" variant="contained" color="primary" fullWidth>
                Guardar
              </Button>
            </Box>
          </form>
        </Box>
      </Modal>
    </div>
  );
};

export default Courses;
