import React, { useState } from 'react';
import { BrowserRouter as Router, Link, Route, Switch } from 'react-router-dom';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { CssBaseline, Drawer, List, ListItem, ListItemText, Collapse, ListItemIcon, Toolbar, Divider, Box, Menu, MenuItem, Button } from '@mui/material';
import { ExpandLess, ExpandMore, People, School, Topic } from '@mui/icons-material';
import Header from './components/Header';
import Body from './components/Body';
import Users from './components/Users';
import Courses from './components/Courses';
import Topics from './components/Topics';
import CreateCourse from './components/CreateCourse';
import './components/Body.css';

const drawerWidth = 240;

const theme = createTheme({
  palette: {
    primary: { main: '#1976d2' },
    secondary: { main: '#ff4081' },
  },
  components: {
    MuiListItem: {
      styleOverrides: {
        root: {
          '&.Mui-selected': {
            backgroundColor: '#1976d2',
            color: 'white',
            '&:hover': { backgroundColor: '#115293' },
          },
          '&:hover': { backgroundColor: '#1976d2', color: 'white' },
        },
      },
    },
    MuiDrawer: {
      styleOverrides: {
        paper: { backgroundColor: '#f5f5f5', color: '#1976d2' },
      },
    },
  },
});

const Dashboard = () => {
  const [openUsers, setOpenUsers] = useState(false);
  const [openCourses, setOpenCourses] = useState(false);
  const [openTopics, setOpenTopics] = useState(false);
  const [anchorEl, setAnchorEl] = useState(null);
  const [openCreateCourse, setOpenCreateCourse] = useState(false);

  const handleUsersClick = () => {
    setOpenUsers(!openUsers);
  };

  const handleCoursesClick = () => {
    setOpenCourses(!openCourses);
  };

  const handleTopicsClick = () => {
    setOpenTopics(!openTopics);
  };

  const handleMenuClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const handleCreateCourseOpen = () => {
    setOpenCreateCourse(true);
  };

  const handleCreateCourseClose = () => {
    setOpenCreateCourse(false);
  };

  return (
    <ThemeProvider theme={theme}>
      <Router>
        <Box sx={{ display: 'flex' }}>
          <CssBaseline />
          <Header handleMenuClick={handleMenuClick} />
          <Drawer
            sx={{
              width: drawerWidth,
              flexShrink: 0,
              '& .MuiDrawer-paper': { width: drawerWidth, boxSizing: 'border-box' },
            }}
            variant="permanent"
            anchor="left"
          >
            <Toolbar />
            <Divider />
            <List>
              <ListItem button onClick={handleUsersClick}>
                <ListItemIcon><People /></ListItemIcon>
                <ListItemText primary="Usuarios" />
                {openUsers ? <ExpandLess /> : <ExpandMore />}
              </ListItem>
              <Collapse in={openUsers} timeout="auto" unmountOnExit>
                <List component="div" disablePadding>
                  <ListItem button component={Link} to="/users">
                    <ListItemText primary="Ver Usuarios" />
                  </ListItem>
                </List>
              </Collapse>
              <ListItem button onClick={handleCoursesClick}>
                <ListItemIcon><School /></ListItemIcon>
                <ListItemText primary="Cursos" />
                {openCourses ? <ExpandLess /> : <ExpandMore />}
              </ListItem>
              <Collapse in={openCourses} timeout="auto" unmountOnExit>
                <List component="div" disablePadding>
                  <ListItem button component={Link} to="/courses">
                    <ListItemText primary="Ver Cursos" />
                  </ListItem>
                  <ListItem button onClick={handleCreateCourseOpen}>
                    <ListItemText primary="Crear Curso" />
                  </ListItem>
                </List>
              </Collapse>
              <ListItem button onClick={handleTopicsClick}>
                <ListItemIcon><Topic /></ListItemIcon>
                <ListItemText primary="Temáticas" />
                {openTopics ? <ExpandLess /> : <ExpandMore />}
              </ListItem>
              <Collapse in={openTopics} timeout="auto" unmountOnExit>
                <List component="div" disablePadding>
                  <ListItem button component={Link} to="/topics">
                    <ListItemText primary="Ver Temáticas" />
                  </ListItem>
                </List>
              </Collapse>
            </List>
          </Drawer>
          <Box component="main" sx={{ flexGrow: 1, bgcolor: 'background.default', p: 3 }}>
            <Toolbar />
            <Switch>
              <Route exact path="/" component={Body} />
              <Route path="/users" component={Users} />
              <Route path="/courses" component={Courses} />
              <Route path="/topics" component={Topics} />
            </Switch>
          </Box>
        </Box>
        <Menu
          anchorEl={anchorEl}
          open={Boolean(anchorEl)}
          onClose={handleMenuClose}
        >
          <MenuItem onClick={handleMenuClose}>Perfil</MenuItem>
          <MenuItem onClick={handleMenuClose}>Mi cuenta</MenuItem>
          <MenuItem onClick={handleMenuClose}>Cerrar sesión</MenuItem>
        </Menu>
        <CreateCourse open={openCreateCourse} handleClose={handleCreateCourseClose} />
      </Router>
    </ThemeProvider>
  );
};

export default Dashboard;
