import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Link, Route, Switch } from 'react-router-dom';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import {
  CssBaseline,
  Drawer,
  List,
  ListItem,
  ListItemText,
  Collapse,
  IconButton,
  Menu,
  MenuItem,
  ListItemIcon,
  Toolbar,
  Divider,
  Box
} from '@mui/material';
import { ExpandLess, ExpandMore, MoreVert, People, School, Topic } from '@mui/icons-material';
import Header from './components/Header';
import Body from './components/Body';
import Users from './components/Users';
import Courses from './components/Courses';
import Topics from './components/Topics';
import './components/Body.css'; // Importar los estilos correctos

const drawerWidth = 240;

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#ff4081',
    },
  },
  components: {
    MuiListItem: {
      styleOverrides: {
        root: {
          '&.Mui-selected': {
            backgroundColor: '#1976d2',
            color: 'white',
            '&:hover': {
              backgroundColor: '#115293',
            },
          },
          '&:hover': {
            backgroundColor: '#1976d2',
            color: 'white',
          },
        },
      },
    },
    MuiDrawer: {
      styleOverrides: {
        paper: {
          backgroundColor: '#f5f5f5',
          color: '#1976d2',
        },
      },
    },
  },
});

const Dashboard = () => {
  const [openUsers, setOpenUsers] = useState(false);
  const [openCourses, setOpenCourses] = useState(false);
  const [openTopics, setOpenTopics] = useState(false);
  const [anchorEl, setAnchorEl] = useState(null);
  const [selectedMenu, setSelectedMenu] = useState('');

  const handleUsersClick = () => {
    setOpenUsers(!openUsers);
    setSelectedMenu('Usuarios');
  };

  const handleCoursesClick = () => {
    setOpenCourses(!openCourses);
    setSelectedMenu('Cursos');
  };

  const handleTopicsClick = () => {
    setOpenTopics(!openTopics);
    setSelectedMenu('Temáticas');
  };

  const handleMenuClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  useEffect(() => {
    // Aquí puedes hacer las llamadas a la API para obtener datos del dashboard
  }, []);

  return (
    <ThemeProvider theme={theme}>
      <Router>
        <Box sx={{ display: 'flex' }}>
          <CssBaseline />
          <Header />
          <Drawer
            variant="permanent"
            sx={{
              width: drawerWidth,
              flexShrink: 0,
              [`& .MuiDrawer-paper`]: { width: drawerWidth, boxSizing: 'border-box', top: '64px' },
            }}
          >
            <Toolbar />
            <Divider />
            <List sx={{ marginTop: '64px' }}>
              <ListItem button onClick={handleUsersClick} selected={selectedMenu === 'Usuarios'}>
                <ListItemIcon>
                  <People sx={{ color: openUsers ? 'white' : '#1976d2' }} />
                </ListItemIcon>
                <ListItemText primary="Usuarios" />
                {openUsers ? <ExpandLess /> : <ExpandMore />}
              </ListItem>
              <Collapse in={openUsers} timeout="auto" unmountOnExit>
                <List component="div" disablePadding>
                  <ListItem button component={Link} to="/users">
                    <ListItemText primary="Listar Usuarios" />
                  </ListItem>
                  <ListItem button component={Link} to="/users/edit">
                    <ListItemText primary="Editar Usuario" />
                  </ListItem>
                  <ListItem button component={Link} to="/users/delete">
                    <ListItemText primary="Eliminar Usuario" />
                  </ListItem>
                </List>
              </Collapse>
              <ListItem button onClick={handleCoursesClick} selected={selectedMenu === 'Cursos'}>
                <ListItemIcon>
                  <School sx={{ color: openCourses ? 'white' : '#1976d2' }} />
                </ListItemIcon>
                <ListItemText primary="Cursos" />
                {openCourses ? <ExpandLess /> : <ExpandMore />}
              </ListItem>
              <Collapse in={openCourses} timeout="auto" unmountOnExit>
                <List component="div" disablePadding>
                  <ListItem button component={Link} to="/courses">
                    <ListItemText primary="Listar Cursos" />
                  </ListItem>
                  <ListItem button component={Link} to="/courses/create">
                    <ListItemText primary="Crear Curso" />
                  </ListItem>
                  <ListItem button component={Link} to="/courses/edit">
                    <ListItemText primary="Editar Curso" />
                  </ListItem>
                  <ListItem button component={Link} to="/courses/assign">
                    <ListItemText primary="Asignar Estudiantes" />
                  </ListItem>
                  <ListItem button component={Link} to="/courses/delete">
                    <ListItemText primary="Eliminar Curso" />
                  </ListItem>
                  <ListItem button component={Link} to="/courses/assign-topic">
                    <ListItemText primary="Asignar Temática" />
                  </ListItem>
                </List>
              </Collapse>
              <ListItem button onClick={handleTopicsClick} selected={selectedMenu === 'Temáticas'}>
                <ListItemIcon>
                  <Topic sx={{ color: openTopics ? 'white' : '#1976d2' }} />
                </ListItemIcon>
                <ListItemText primary="Temáticas" />
                {openTopics ? <ExpandLess /> : <ExpandMore />}
              </ListItem>
              <Collapse in={openTopics} timeout="auto" unmountOnExit>
                <List component="div" disablePadding>
                  <ListItem button component={Link} to="/topics">
                    <ListItemText primary="Listar Temáticas" />
                  </ListItem>
                  <ListItem button component={Link} to="/topics/create">
                    <ListItemText primary="Crear Temática" />
                  </ListItem>
                  <ListItem button component={Link} to="/topics/edit">
                    <ListItemText primary="Editar Temática" />
                  </ListItem>
                  <ListItem button component={Link} to="/topics/assign">
                    <ListItemText primary="Asignar Temática" />
                  </ListItem>
                  <ListItem button component={Link} to="/topics/delete">
                    <ListItemText primary="Eliminar Temática" />
                  </ListItem>
                </List>
              </Collapse>
            </List>
            <IconButton onClick={handleMenuClick}>
              <MoreVert />
            </IconButton>
            <Menu
              anchorEl={anchorEl}
              open={Boolean(anchorEl)}
              onClose={handleMenuClose}
            >
              <MenuItem onClick={handleMenuClose}>Perfil</MenuItem>
              <MenuItem onClick={handleMenuClose}>Cerrar sesión</MenuItem>
            </Menu>
          </Drawer>
          <Box
            component="main"
            sx={{ flexGrow: 1, p: 3, width: { sm: `calc(100% - ${drawerWidth}px)` }, marginTop: '64px' }}
          >
            <Switch>
              <Route path="/" exact component={Body} />
              <Route path="/users" component={Users} />
              <Route path="/courses" component={Courses} />
              <Route path="/topics" component={Topics} />
              {/* Agregar otras rutas aquí */}
            </Switch>
          </Box>
        </Box>
      </Router>
    </ThemeProvider>
  );
};

export default Dashboard;
